DROP TABLE IF EXISTS `bookclass`;
CREATE TABLE `bookclass` (
  `classname` varchar(10) DEFAULT NULL,
  `detail` varchar(255) DEFAULT NULL COMMENT '说明',
  `classno` int(11) NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`classno`)
) ENGINE=InnoDB AUTO_INCREMENT=118016 DEFAULT CHARSET=utf8;

#
# Data for table "bookclass"
#

INSERT INTO `bookclass` VALUES ('编程语言','编程语言（programming language），是用来定义计算机程序的形式语言。它是一种被标准化的交流技巧，用来向计算机发出指令。一种计算机语言让程序员能够准确地定义计算机所需要使用的数据，并精确地定义在不同情况下所应当采取的行动。',118001),('数据库','数据库（Database）是按照数据结构来组织、存储和管理数据的仓库，它产生于距今五十年前，随着信息技术和市场的发展，特别是二十世纪九十年代以后，数据管理不再仅仅是存储和管理数据，而转变成用户所需要的各种数据管理的方式。数据库有很多种类型，从最简单的存储有各种数据的表格到能够进行海量数据存储的大型数据库系统都在各个方面得到了广泛的应用。',118002),('社会小说','社会小说特点是：背景广阔，贴近生活，描写细腻。',118004),('中国科幻','中国科幻小说是一种具有文学艺术性的科普读物。',118005),('前端设计','网页前端设计，一般涉及到HTML，CSS，javascrip。。。',118015);

#
# Structure for table "bookinfo"
#

DROP TABLE IF EXISTS `bookinfo`;
CREATE TABLE `bookinfo` (
  `bno` varchar(20) NOT NULL DEFAULT '' COMMENT '每一本书的唯一编码',
  `Book` int(11) unsigned NOT NULL DEFAULT '100001' COMMENT '同种书的唯一编码，即同本书同编码',
  `bname` char(20) DEFAULT NULL,
  `author` varchar(40) NOT NULL DEFAULT '佚名',
  `descr` varchar(512) NOT NULL DEFAULT '无简介' COMMENT '该书的简介',
  `price` float DEFAULT NULL,
  `classno` int(11) DEFAULT NULL COMMENT '类别号',
  `isbn` char(20) DEFAULT NULL COMMENT 'ISBN序列号',
  `nows` int(1) unsigned NOT NULL DEFAULT '1' COMMENT '借走为0',
  `Search` int(8) NOT NULL DEFAULT '0' COMMENT '被搜索的次数',
  `Borrowed` int(11) NOT NULL DEFAULT '0',
  `cover` varchar(255) DEFAULT 'img/Cover/.jpg' COMMENT '书封面图片的URL地址',
  PRIMARY KEY (`bno`),
  KEY `F_N` (`classno`),
  CONSTRAINT `F_N` FOREIGN KEY (`classno`) REFERENCES `bookclass` (`classno`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

#
# Data for table "bookinfo"
#

INSERT INTO `bookinfo` VALUES ('1470001',100001,'Oracle数据库教程','陆万','Oracle 数据库教程和应用系统开发已经成为国内外高校计算机专业和许多非计算机专业的必修或选修课程。《Oracle数据库教程》结合大量的实例，介绍如何利用Oracle来管理和维护数据，以及使用各种开发工具开发C/S（Client/Server）模式和B/S（Browser/Server）模式网络数据库网络应用程序',49,118002,'9787302400707',1,1,0,'img/Cover/9787302400707.jpg'),('1470002',100001,'Oracle数据库教程','陆万','Oracle 数据库教程和应用系统开发已经成为国内外高校计算机专业和许多非计算机专业的必修或选修课程。《Oracle数据库教程》结合大量的实例，介绍如何利用Oracle来管理和维护数据，以及使用各种开发工具开发C/S（Client/Server）模式和B/S（Browser/Server）模式网络数据库网络应用程序',49,118002,'9787302400708',1,1,0,'img/Cover/9787302400707.jpg'),('1470003',100001,'Oracle数据库教程','陆万','Oracle 数据库教程和应用系统开发已经成为国内外高校计算机专业和许多非计算机专业的必修或选修课程。《Oracle数据库教程》结合大量的实例，介绍如何利用Oracle来管理和维护数据，以及使用各种开发工具开发C/S（Client/Server）模式和B/S（Browser/Server）模式网络数据库网络应用程序',49,118002,'9787302400709',1,1,0,'img/Cover/9787302400707.jpg'),('1470004',100001,'Oracle数据库教程','陆万','Oracle 数据库教程和应用系统开发已经成为国内外高校计算机专业和许多非计算机专业的必修或选修课程。《Oracle数据库教程》结合大量的实例，介绍如何利用Oracle来管理和维护数据，以及使用各种开发工具开发C/S（Client/Server）模式和B/S（Browser/Server）模式网络数据库网络应用程序',49,118002,'9787302400710',1,1,0,'img/Cover/9787302400707.jpg'),('1470005',100002,'The Little Prince','万·德·圣·埃克·苏赔里','《小王子》是法国作家安托万*德*圣*埃克*苏赔里 于1942年写成的著名儿童文学短篇小说。本书的主人公是来自外星球的小王子。书中以一位飞行员作为故事叙诉者，讲述了小王子从自己星球出发前往地球的过程中，所经历的各种历险。作者以小王子的孩子式的眼光，透视出成人的空虚、盲目、愚妄和死板教条，用浅显天真的语言写出了人类的孤独寂寞、没有根基随风流浪的命运。同时，也表达出作者对金钱关系的批判，对真善美的讴歌。 ',29,118004,'9787511024848',0,60,6,'img/Cover/9787511024848.jpg'),('1470006',100002,'The Little Prince','万·德·圣·埃克·苏赔里','《小王子》是法国作家安托万*德*圣*埃克*苏赔里 于1942年写成的著名儿童文学短篇小说。本书的主人公是来自外星球的小王子。书中以一位飞行员作为故事叙诉者，讲述了小王子从自己星球出发前往地球的过程中，所经历的各种历险。作者以小王子的孩子式的眼光，透视出成人的空虚、盲目、愚妄和死板教条，用浅显天真的语言写出了人类的孤独寂寞、没有根基随风流浪的命运。同时，也表达出作者对金钱关系的批判，对真善美的讴歌。 ',29,118004,'9787511024849',1,6,8,'img/Cover/9787511024848.jpg'),('1470007',100003,'JavaScript王者归来','吴亮','作者简介：吴亮，（网名月影），早年曾在微软亚洲研究院做过访问学生、在金蝶软件有限公司先后担任过核心开发工程师、设计师和项目经理，现任百度电子商务事业部Web开发项目经理。多年来致力于JavaScript技术和Web标准的推广，活跃于国内极有影响力的JavaScript专业网站——无忧脚本，并任JavaScript版的版主。平时热爱文学、写作和围棋',86,118001,'9787302173083',1,0,9,'img/Cover/9787302173083.jpg'),('1470008',100003,'JavaScript王者归来','吴亮','作者简介：吴亮，（网名月影），早年曾在微软亚洲研究院做过访问学生、在金蝶软件有限公司先后担任过核心开发工程师、设计师和项目经理，现任百度电子商务事业部Web开发项目经理。多年来致力于JavaScript技术和Web标准的推广，活跃于国内极有影响力的JavaScript专业网站——无忧脚本，并任JavaScript版的版主。平时热爱文学、写作和围棋',86,118001,'9787302173083',1,0,0,'img/Cover/9787302173083.jpg'),('1470009',100004,'Jane   Eyre','夏洛蒂·勃朗特','《简·爱》（Jane Eyre）是十九世纪英国著名的女作家夏洛蒂·勃朗特的代表作，人们普遍认为《简·爱》是夏洛蒂·勃朗特“诗意的生平写照”，是一部具有自传色彩的作品。讲述一位从小变成孤儿的英国女子在各种磨难中不断追求自由与尊严，坚持自我，最终获得幸福的故事。小说引人入胜地展示了男女主人公曲折起伏的爱情经历，歌颂了摆脱一切旧习俗和偏见，成功塑造了一个敢于反抗，敢于争取自由和平等地位的妇女形象。',36,118004,'9787478931473',1,5,0,'img/Cover/9787478931473.jpg'),('1470010',100004,'Jane   Eyre','夏洛蒂·勃朗特','《简·爱》（Jane Eyre）是十九世纪英国著名的女作家夏洛蒂·勃朗特的代表作，人们普遍认为《简·爱》是夏洛蒂·勃朗特“诗意的生平写照”，是一部具有自传色彩的作品。讲述一位从小变成孤儿的英国女子在各种磨难中不断追求自由与尊严，坚持自我，最终获得幸福的故事。小说引人入胜地展示了男女主人公曲折起伏的爱情经历，歌颂了摆脱一切旧习俗和偏见，成功塑造了一个敢于反抗，敢于争取自由和平等地位的妇女形象。',36,118004,'9787478931473',1,5,0,'img/Cover/9787478931473.jpg'),('1470011',100004,'Jane   Eyre','夏洛蒂·勃朗特','《简·爱》（Jane Eyre）是十九世纪英国著名的女作家夏洛蒂·勃朗特的代表作，人们普遍认为《简·爱》是夏洛蒂·勃朗特“诗意的生平写照”，是一部具有自传色彩的作品。讲述一位从小变成孤儿的英国女子在各种磨难中不断追求自由与尊严，坚持自我，最终获得幸福的故事。小说引人入胜地展示了男女主人公曲折起伏的爱情经历，歌颂了摆脱一切旧习俗和偏见，成功塑造了一个敢于反抗，敢于争取自由和平等地位的妇女形象。',36,118004,'9787478931473',1,45,5,'img/Cover/9787478931473.jpg'),('1470012',100005,'Ext JS实战','Jesus Garcia','本书适合任何想要学习并使用Ext JS 且具有一定的javascript开发经验的开发人员阅读参考',79,118001,'9787115395924',1,5,0,'img/Cover/9787115395924.jpg'),('1470013',100005,'Ext JS实战','Jesus Garcia','本书适合任何想要学习并使用Ext JS 且具有一定的javascript开发经验的开发人员阅读参考',79,118001,'9787115395924',1,5,0,'img/Cover/9787115395924.jpg'),('1470014',100006,'代码之髓','曾一鸣','本书作者从编程语言设计的角度出发，围绕语言中共通或特有的核心概念，通过语言演变过程中的纵向比较和在多门语言中的横向比较，清晰地呈现了程序设计语言中函数、类型、作用域、类、继承等核心知识。本书旨在帮助读者更好地理解各种概念是因何而起，并在此基础上更好地判断为何使用、何时使用及怎样使用。',45,118001,'9787115361530',1,4,0,'img/Cover/9787115361530.jpg'),('1470015',100006,'代码之髓','曾一鸣','本书作者从编程语言设计的角度出发，围绕语言中共通或特有的核心概念，通过语言演变过程中的纵向比较和在多门语言中的横向比较，清晰地呈现了程序设计语言中函数、类型、作用域、类、继承等核心知识。本书旨在帮助读者更好地理解各种概念是因何而起，并在此基础上更好地判断为何使用、何时使用及怎样使用。',45,118001,'9787115361530',0,4,1,'img/Cover/9787115361530.jpg'),('1470016',100006,'代码之髓','曾一鸣','本书作者从编程语言设计的角度出发，围绕语言中共通或特有的核心概念，通过语言演变过程中的纵向比较和在多门语言中的横向比较，清晰地呈现了程序设计语言中函数、类型、作用域、类、继承等核心知识。本书旨在帮助读者更好地理解各种概念是因何而起，并在此基础上更好地判断为何使用、何时使用及怎样使用。',45,118001,'9787115361530',1,4,0,'img/Cover/9787115361530.jpg'),('1470017',100007,'代码之殇','Eric Brechner','本书是《代码大全》的姊妹篇，资深软件开发专家30余年工作经验结晶，被誉为 软件行业的财富 ，微软公司软件工程师必读之书。它从软件开发流程、技术、方法、项目管理、团队管理、人际沟通等多角度总结出90余个具有代表性的问题（大多数问题可能会给公司或软件项目带来毁灭性灾难），并给出了问题的解决方案和最佳实践，值得所有软件工程师和项目管理者研读。',79,118001,'9787111416821',1,4,0,'img/Cover/9787111416821.jpg'),('1470018',100007,'代码之殇','Eric Brechner','本书是《代码大全》的姊妹篇，资深软件开发专家30余年工作经验结晶，被誉为 软件行业的财富 ，微软公司软件工程师必读之书。它从软件开发流程、技术、方法、项目管理、团队管理、人际沟通等多角度总结出90余个具有代表性的问题（大多数问题可能会给公司或软件项目带来毁灭性灾难），并给出了问题的解决方案和最佳实践，值得所有软件工程师和项目管理者研读。',79,118001,'9787111416821',1,4,0,'img/Cover/9787111416821.jpg'),('1470019',100007,'代码之殇','Eric Brechner','本书是《代码大全》的姊妹篇，资深软件开发专家30余年工作经验结晶，被誉为 软件行业的财富 ，微软公司软件工程师必读之书。它从软件开发流程、技术、方法、项目管理、团队管理、人际沟通等多角度总结出90余个具有代表性的问题（大多数问题可能会给公司或软件项目带来毁灭性灾难），并给出了问题的解决方案和最佳实践，值得所有软件工程师和项目管理者研读。',79,118001,'9787111416821',1,4,0,'img/Cover/9787111416821.jpg'),('1470020',100008,'Thinking in java','Bruce Eckel','本书赢得了全球程序员的广泛赞誉，即使是最晦涩的概念，在Bruce Eckel的文字亲和力和小而直接的编程示例面前也会化解于无形。从Java的基础语法到最高级特性（深入的面向对象概念、多线程、自动项目构建、单元测试和调试等），本书都能逐步指导你轻松掌握。',79,118001,'9780131872486',1,4,0,'img/Cover/9780131872486.jpg'),('1470021',100008,'Thinking in java','Bruce Eckel','本书赢得了全球程序员的广泛赞誉，即使是最晦涩的概念，在Bruce Eckel的文字亲和力和小而直接的编程示例面前也会化解于无形。从Java的基础语法到最高级特性（深入的面向对象概念、多线程、自动项目构建、单元测试和调试等），本书都能逐步指导你轻松掌握。',79,118001,'9780131872486',1,4,0,'img/Cover/9780131872486.jpg'),('1470022',100008,'Thinking in java','Bruce Eckel','本书赢得了全球程序员的广泛赞誉，即使是最晦涩的概念，在Bruce Eckel的文字亲和力和小而直接的编程示例面前也会化解于无形。从Java的基础语法到最高级特性（深入的面向对象概念、多线程、自动项目构建、单元测试和调试等），本书都能逐步指导你轻松掌握。',79,118001,'9780131872486',1,4,0,'img/Cover/9780131872486.jpg'),('1470023',100008,'Thinking in java','Bruce Eckel','本书赢得了全球程序员的广泛赞誉，即使是最晦涩的概念，在Bruce Eckel的文字亲和力和小而直接的编程示例面前也会化解于无形。从Java的基础语法到最高级特性（深入的面向对象概念、多线程、自动项目构建、单元测试和调试等），本书都能逐步指导你轻松掌握。',79,118001,'9780131872486',1,4,0,'img/Cover/9780131872486.jpg'),('1470024',100009,'The Catcher in Rye','杰罗姆·大卫·塞林格','《麦田里的守望者》原作名《The Catcher in the Rye》是美国作家杰罗姆·大卫·塞林格唯一的一部长篇小说，塞林格将故事的起止局限于16岁的中学生霍尔顿·考尔菲德从离开学校到纽约游荡的三天时间内，并借鉴了意识流天马行空的写作方法，充分探索了一个十几岁少年的内心世界。愤怒与焦虑是此书的两大主题，主人公的经历和思想在青少年中引起强烈共鸣，受到读者，特别是广大中学生的热烈欢迎。',57,118004,'9787544711296',1,7,0,'img/Cover/9787544711296.jpg'),('1470025',100009,'The Catcher in Rye','杰罗姆·大卫·塞林格','《麦田里的守望者》原作名《The Catcher in the Rye》是美国作家杰罗姆·大卫·塞林格唯一的一部长篇小说，塞林格将故事的起止局限于16岁的中学生霍尔顿·考尔菲德从离开学校到纽约游荡的三天时间内，并借鉴了意识流天马行空的写作方法，充分探索了一个十几岁少年的内心世界。愤怒与焦虑是此书的两大主题，主人公的经历和思想在青少年中引起强烈共鸣，受到读者，特别是广大中学生的热烈欢迎。',57,118004,'9787544711296',0,7,1,'img/Cover/9787544711296.jpg'),('1470026',100009,'The Catcher in Rye','杰罗姆·大卫·塞林格','《麦田里的守望者》原作名《The Catcher in the Rye》是美国作家杰罗姆·大卫·塞林格唯一的一部长篇小说，塞林格将故事的起止局限于16岁的中学生霍尔顿·考尔菲德从离开学校到纽约游荡的三天时间内，并借鉴了意识流天马行空的写作方法，充分探索了一个十几岁少年的内心世界。愤怒与焦虑是此书的两大主题，主人公的经历和思想在青少年中引起强烈共鸣，受到读者，特别是广大中学生的热烈欢迎。',57,118004,'9787544711296',1,7,0,'img/Cover/9787544711296.jpg'),('1470027',100010,'平凡的世界','路遥','这是一部全景式地表现中国当代城乡社会生活的长篇小说。全书共三部。作者在近十年间广阔背景上，通过复杂的矛盾纠葛，刻划了社会各阶层众多普通人的形象。劳动与爱情，挫折与追求，痛苦与欢乐，日常生活与巨大社会冲突，纷繁地交织在一起，深刻地展示了普通人在大时代历史进程中所走过的艰难曲折的道路。',60,118004,'9787020044528',1,2,0,'img/Cover/9787020044528.jpg'),('1470028',100010,'平凡的世界','路遥','这是一部全景式地表现中国当代城乡社会生活的长篇小说。全书共三部。作者在近十年间广阔背景上，通过复杂的矛盾纠葛，刻划了社会各阶层众多普通人的形象。劳动与爱情，挫折与追求，痛苦与欢乐，日常生活与巨大社会冲突，纷繁地交织在一起，深刻地展示了普通人在大时代历史进程中所走过的艰难曲折的道路。',60,118004,'9787020044528',0,2,1,'img/Cover/9787020044528.jpg'),('1470029',100010,'平凡的世界','路遥','这是一部全景式地表现中国当代城乡社会生活的长篇小说。全书共三部。作者在近十年间广阔背景上，通过复杂的矛盾纠葛，刻划了社会各阶层众多普通人的形象。劳动与爱情，挫折与追求，痛苦与欢乐，日常生活与巨大社会冲突，纷繁地交织在一起，深刻地展示了普通人在大时代历史进程中所走过的艰难曲折的道路。',60,118004,'9787020044528',1,2,0,'img/Cover/9787020044528.jpg'),('1470030',100011,'三体·地球往事','刘慈欣','文化大革命如火如荼进行的同时，军方探寻外星文明的绝秘计划《红岸工程》取得了突破性进展。但在按下发射键的那一刻，历经劫难的叶文洁没有意识到，她彻底改变了人类的命运　地球文明向宇宙发出的一声啼鸣，以太阳为中心，以光速向宇宙深处飞驰……　四光年外，三体文明 正苦苦挣扎——三颗无规则运行的太阳主导下的百余次毁灭与重生逼迫他们逃离母星。而恰在此时，他们接收到了地球发来的信息。　在运用超技术锁死地球人的基础科学之后，三体人庞大的宇宙舰队开始向地球进发……人类的末日悄然来临。',23,118005,'9787536692930',1,14,1,'img/Cover/9787536692930.jpg'),('1470031',100012,'三体·黑暗森林','刘慈欣','三体人在利用魔法般的科技锁死了地球人的科学之后，庞大的宇宙舰队杀气腾腾地直扑太阳系，意欲清除地球文明。面对前所未有的危局，经历过无数磨难的地球人组建起同样庞大的太空舰队，同时，利用三体人思维透明的致命缺陷，制订了神秘莫测的“面壁计划”，精选出四位 *面壁者*。秘密展开对三体人的反击。',34,118005,'9787536693968',1,9,1,'img/Cover/9787536693968.jpg'),('1470032',100013,'三体·死神永生','刘慈欣','与三体文明的战争使人类看到了宇宙黑暗的真相，地球文明像一个恐惧的孩子，熄灭了寻友的篝火，在暗夜中发抖。自以为历经沧桑，其实刚刚蹒跚学步；自以为悟出了生存竞争的秘密，其实还远没有竞争的资格。使两个文明命悬一线的黑暗森林打击，不过是宇宙战场上的一个微不足道的插曲，一个在战场上乱跑的无知孩童被堑壕中的狙击手射杀，仅此而已。真正的星际战争没人见过，也不可能见到。因为战争的方式和武器已经远超出人类的想象，目睹战场之日，就是灭亡之时。',45,118005,'9787229030933',1,7,0,'img/Cover/9787229030933.jpg'),('1470033',100014,'寂静的春天','蕾切尔·卡森 ','《寂静的春天》是美国女作家蕾切儿·卡逊的代表作，也是50年以来全球最具影响的著作之一！《寂静的春天》以寓言开头向我们描绘了一个美丽村庄的突变，并从陆地到海洋，从海洋到天空，全方位地揭示了化学农药的危害，是一本公认的开启了世界环境运动的奠基之作，它既贯穿着严谨求实的科学理性精神，又充溢着敬畏生命的人文情怀，是一本赏心悦目的著作。',28,118004,'9787532742189',0,4,1,'img/Cover/9787532742189.jpg'),('1470034',100013,'三体·死神永生','刘慈欣','与三体文明的战争使人类看到了宇宙黑暗的真相，地球文明像一个恐惧的孩子，熄灭了寻友的篝火，在暗夜中发抖。自以为历经沧桑，其实刚刚蹒跚学步；自以为悟出了生存竞争的秘密，其实还远没有竞争的资格。使两个文明命悬一线的黑暗森林打击，不过是宇宙战场上的一个微不足道的插曲，一个在战场上乱跑的无知孩童被堑壕中的狙击手射杀，仅此而已。真正的星际战争没人见过，也不可能见到。因为战争的方式和武器已经远超出人类的想象，目睹战场之日，就是灭亡之时。',45,118005,'9787229030933',1,7,0,'img/Cover/9787229030933.jpg'),('1470035',100013,'三体·死神永生','刘慈欣','与三体文明的战争使人类看到了宇宙黑暗的真相，地球文明像一个恐惧的孩子，熄灭了寻友的篝火，在暗夜中发抖。自以为历经沧桑，其实刚刚蹒跚学步；自以为悟出了生存竞争的秘密，其实还远没有竞争的资格。使两个文明命悬一线的黑暗森林打击，不过是宇宙战场上的一个微不足道的插曲，一个在战场上乱跑的无知孩童被堑壕中的狙击手射杀，仅此而已。真正的星际战争没人见过，也不可能见到。因为战争的方式和武器已经远超出人类的想象，目睹战场之日，就是灭亡之时。',45,118005,'9787229030933',1,7,1,'img/Cover/9787229030933.jpg'),('1470036',100013,'三体·死神永生','刘慈欣','与三体文明的战争使人类看到了宇宙黑暗的真相，地球文明像一个恐惧的孩子，熄灭了寻友的篝火，在暗夜中发抖。自以为历经沧桑，其实刚刚蹒跚学步；自以为悟出了生存竞争的秘密，其实还远没有竞争的资格。使两个文明命悬一线的黑暗森林打击，不过是宇宙战场上的一个微不足道的插曲，一个在战场上乱跑的无知孩童被堑壕中的狙击手射杀，仅此而已。真正的星际战争没人见过，也不可能见到。因为战争的方式和武器已经远超出人类的想象，目睹战场之日，就是灭亡之时。',45,118005,'9787229030933',1,7,0,'img/Cover/9787229030933.jpg'),('1470037',100013,'三体·死神永生','刘慈欣','与三体文明的战争使人类看到了宇宙黑暗的真相，地球文明像一个恐惧的孩子，熄灭了寻友的篝火，在暗夜中发抖。自以为历经沧桑，其实刚刚蹒跚学步；自以为悟出了生存竞争的秘密，其实还远没有竞争的资格。使两个文明命悬一线的黑暗森林打击，不过是宇宙战场上的一个微不足道的插曲，一个在战场上乱跑的无知孩童被堑壕中的狙击手射杀，仅此而已。真正的星际战争没人见过，也不可能见到。因为战争的方式和武器已经远超出人类