#
# Structure for table "users"
#

DROP TABLE IF EXISTS `users`;
CREATE TABLE `users` (
  `uno` char(10) NOT NULL,
  `uname` char(10) DEFAULT NULL,
  `pass` char(16) NOT NULL DEFAULT '',
  `usex` char(2) DEFAULT NULL,
  `uage` int(11) DEFAULT NULL,
  `ucredit` int(11) DEFAULT NULL COMMENT '借书的最大值',
  `identity` char(10) DEFAULT NULL COMMENT '身份',
  `owe` int(11) DEFAULT NULL COMMENT '欠款',
  `uemail` char(30) DEFAULT NULL,
  `chat` varchar(60) DEFAULT NULL COMMENT '读书宣言',
  `borrowed` int(11) NOT NULL DEFAULT '0' COMMENT '已经借书数量',
  `image` varchar(255) DEFAULT NULL COMMENT '200*200头像图片',
  `total` int(11) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`uno`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

