#
# Structure for table "borrow"
#

DROP TABLE IF EXISTS `borrow`;
CREATE TABLE `borrow` (
  `uno` char(10) DEFAULT NULL,
  `bno` varchar(20) DEFAULT NULL,
  `boDate` date DEFAULT NULL,
  `reDate` date DEFAULT NULL COMMENT '应该归还的日期',
  `factDate` date DEFAULT NULL COMMENT '实际归还的日期',
  `overDays` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '超期天数，计算罚款',
  `fee` float(3,1) NOT NULL DEFAULT '0.1' COMMENT '缴纳的罚款的单价',
  `already` int(2) NOT NULL DEFAULT '1' COMMENT '是否缴清罚款1是0否',
  KEY `F_B` (`uno`),
  KEY `F_U` (`bno`),
  CONSTRAINT `F_B` FOREIGN KEY (`uno`) REFERENCES `users` (`uno`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `F_U` FOREIGN KEY (`bno`) REFERENCES `bookinfo` (`bno`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='这是未归还的书的表格';

