DROP VIEW IF EXISTS `backbook`;
CREATE VIEW `backbook` AS 
  select `u`.`uname` AS `uname`,`bi`.`bname` AS `bname`,`bo`.`uno` AS `uno`,`bo`.`bno` AS `bno`,`bo`.`boDate` AS `boDate`,`bo`.`reDate` AS `reDate`,`bo`.`factDate` AS `factDate`,`bo`.`overDays` AS `overDays`,`bo`.`fee` AS `fee`,`bo`.`already` AS `already`,(`bo`.`fee` * `bo`.`overDays`) AS `fees` from ((`backed` `bo` join `users` `u`) join `bookinfo` `bi`) where ((`bo`.`uno` = `u`.`uno`) and (`bi`.`bno` = `bo`.`bno`));

#
# Structure for table "bobooks"
#

DROP VIEW IF EXISTS `bobooks`;
CREATE VIEW `bobooks` AS 
  select `bi`.`bno` AS `bno`,`bi`.`nows` AS `nows`,`bi`.`Book` AS `book`,`bi`.`bname` AS `bname`,`bc`.`classname` AS `classname`,`bo`.`reDate` AS `reDate` from (`bookclass` `bc` join (`bookinfo` `bi` left join `borrow` `bo` on((`bo`.`bno` = `bi`.`bno`)))) where (`bi`.`classno` = `bc`.`classno`);

#
# Structure for table "bookes"
#

DROP VIEW IF EXISTS `bookes`;
CREATE VIEW `bookes` AS 
  select `bi`.`bno` AS `bno`,`bi`.`Book` AS `Book`,`bi`.`bname` AS `bname`,`bi`.`author` AS `author`,`bi`.`descr` AS `descr`,`bi`.`price` AS `price`,`bi`.`classno` AS `classno`,`bi`.`isbn` AS `isbn`,`bi`.`nows` AS `nows`,`bi`.`Search` AS `Search`,`bi`.`Borrowed` AS `Borrowed`,`bi`.`cover` AS `cover`,`bc`.`classname` AS `classname` from (`bookinfo` `bi` left join `bookclass` `bc` on((`bi`.`classno` = `bc`.`classno`)));

#
# Structure for table "usebook"
#

DROP VIEW IF EXISTS `usebook`;
CREATE VIEW `usebook` AS 
  select `u`.`uname` AS `uname`,`bi`.`bname` AS `bname`,`bo`.`uno` AS `uno`,`bo`.`bno` AS `bno`,`bo`.`boDate` AS `boDate`,`bo`.`reDate` AS `reDate`,`bo`.`factDate` AS `factDate`,`bo`.`overDays` AS `overDays`,`bo`.`fee` AS `fee`,`bo`.`already` AS `already`,(`bo`.`fee` * `bo`.`overDays`) AS `fees` from ((`borrow` `bo` join `users` `u`) join `bookinfo` `bi`) where ((`bo`.`uno` = `u`.`uno`) and (`bi`.`bno` = `bo`.`bno`));
